<script lang="ts" type="module">
	import { onMount } from "svelte";
	import * as data from "./knowledgebase.json";
	import * as d3 from "https://cdn.jsdelivr.net/npm/d3@7/+esm";
	import { fade } from "svelte/transition";

	const marginTop = 10;
	const marginRight = 10;
	const marginBottom = 10;
	const marginLeft = 5;
	let first_clicked = false;
	let svg; // Define svg variable outside of functions for access.

	onMount(() => {
		const root = d3.hierarchy(data);

		const dx = 20;
		const dy = (window.innerWidth - marginRight - marginLeft) / (1 + root.height);

		const tree = d3.tree().nodeSize([dx, dy]);
		const diagonal = d3
			.linkHorizontal()
			.x((d) => d.y)
			.y((d) => d.x);

		svg = d3.create("svg").attr("viewBox", [0, 0, window.innerWidth, window.innerHeight]);

		const gLink = svg
			.append("g")
			.attr("fill", "none")
			.attr("stroke", "#888")
			.attr("stroke-opacity", 0.4)
			.attr("stroke-width", 2);

		const gNode = svg.append("g").attr("cursor", "pointer").attr("pointer-events", "all");

		function update(event, source) {
			const duration = event?.altKey ? 2500 : 500; // hold the alt key to slow down the transition
			const nodes = root.descendants().reverse();
			const links = root.links();

			// Compute the new tree layout.
			tree(root);

			let left = root;
			let right = root;
			root.eachBefore((node) => {
				if (node.x < left.x) left = node;
				if (node.x > right.x) right = node;
			});

			const height = right.x - left.x + marginTop + marginBottom;

			const transition = svg
				.transition()
				.duration(duration)
				.attr("height", height)
				.attr("viewBox", [-marginLeft, left.x - marginTop, window.innerWidth, height]);

			// Update the nodes…
			const node = gNode.selectAll("g").data(nodes, (d) => d.id);

			// Enter any new nodes at the parent's previous position.
			const nodeEnter = node
				.enter()
				.append("g")
				.attr("transform", (d) => `translate(${source.y0},${source.x0})`)
				.attr("fill-opacity", 0)
				.attr("stroke-opacity", 0)
				.attr("tabindex", "0")
				.attr("role", "button")
				.on("click", (event: Event, d: any) => {
					d.children = d.children ? null : d._children;
					first_clicked = true;
					update(event, d);
				});

			nodeEnter
				.append("circle")
				.attr("r", 5)
				.attr("fill", (d) => (d._children ? "#555" : "#999"))
				.attr("stroke-width", 10);

			nodeEnter
				.append("text")
				.attr("dy", "0.20em")
				.attr("x", (d) => (d._children ? -10 : 10))
				.attr("text-anchor", (d) => (d._children ? "end" : "start"))
				.text((d) => d.data.name)
				.attr("stroke-linejoin", "round")
				.attr("font-size", "1em")
				.attr("font-weight", "bold")
				.attr("fill", "white")
				.attr("paint-order", "stroke");

			// Transition nodes to their new position.
			const nodeUpdate = node
				.merge(nodeEnter)
				.transition(transition)
				.attr("transform", (d) => `translate(${d.y},${d.x})`)
				.attr("fill-opacity", 1)
				.attr("stroke-opacity", 1);

			// Transition exiting nodes to the parent's new position.
			const nodeExit = node
				.exit()
				.transition(transition)
				.remove()
				.attr("transform", (d) => `translate(${source.y},${source.x})`)
				.attr("fill-opacity", 0)
				.attr("stroke-opacity", 0);

			// Update the links…
			const link = gLink.selectAll("path").data(links, (d) => d.target.id);

			// Enter any new links at the parent's previous position.
			const linkEnter = link
				.enter()
				.append("path")
				.attr("d", (d) => {
					const o = { x: source.x0, y: source.y0 };
					return diagonal({ source: o, target: o });
				});

			// Transition links to their new position.
			link.merge(linkEnter).transition(transition).attr("d", diagonal);

			// Transition exiting nodes to the parent's new position.
			link
				.exit()
				.transition(transition)
				.remove()
				.attr("d", (d) => {
					const o = { x: source.x, y: source.y };
					return diagonal({ source: o, target: o });
				});

			// Stash the old positions for transition.
			root.eachBefore((d) => {
				d.x0 = d.x;
				d.y0 = d.y;
			});
		}

		// Do the first update to the initial configuration of the tree
		root.x0 = dy / 2;
		root.y0 = 0;
		root.descendants().forEach((d, i) => {
			d.id = i;
			d._children = d.children;
			if (d.depth && d.data.name.length !== 7) d.children = null;
		});

		update(null, root);

		// Append the SVG element.
		let container = document.getElementById("knowledge-chart");
		container.prepend(svg.node());

		// Handle window resize to adjust SVG size dynamically
		window.addEventListener("resize", () => {
			const dx = 20;
			const dy = (window.innerWidth - marginRight - marginLeft) / (1 + root.height);

			svg.attr("viewBox", [0, 0, window.innerWidth, window.innerHeight]);

			tree.nodeSize([dx, dy]);
			update(null, root);
		});
	});
</script>

